import { Constants } from './../../../utilities/constants';
import { ACADEMICLEVELSUBJECTLIST, ACADEMICLEVELIDLIST, ACADEMICSUBJECTIDLIST,  } from './../../../utilities/code-table/AcademicLevelSubjectList';
import { Component } from '@angular/core';
import { CreateListingForm } from 'src/app/DTO/CreateListingForm';
import { RestclientService } from 'src/app/services/restclient.service';
import { CodeTable } from 'src/app/utilities/code-table/CodeTable';


@Component({
  selector: 'app-create-listing',
  templateUrl: './create-listing.component.html',
  styleUrls: ['./create-listing.component.css']
})
export class CreateListingComponent {

  createListingForm: CreateListingForm = new CreateListingForm();
  academicLvlList: CodeTable[] = ACADEMICLEVELIDLIST;
  academicLvlSubjectList: string[];
  academicSubjectList: Map<String, String> = ACADEMICSUBJECTIDLIST;
  address: string;
  dayOfWeek: number[] = [0,0,0,0,0,0,0];



  constructor(private restClient: RestclientService) { }

  ngOnInit(){

    this.createListingForm.acadLvl = "P1";
    //call userservice or get from localstorage after login to get user academic level


    ACADEMICLEVELSUBJECTLIST.forEach((lvlSubj) => {
      if(lvlSubj.acadLvlId === this.createListingForm.acadLvl){
        this.academicLvlSubjectList = lvlSubj.acadSubjectIdList;
      }
    })

    this.createListingForm.postalCode = "200640";
    //call userservice or get from localstorage after login to get user postal code

    this.restClient.getrawjson(Constants.oneMapURLStart + this.createListingForm.postalCode + Constants.oneMapURLEnd)
          .then((res) => {
            this.address = res.results[0].ADDRESS;
          })


  }


  flipDayIndex(day: number){
      this.dayOfWeek[day] ? this.dayOfWeek[day] = 0 : this.dayOfWeek[day] = 1;
      alert(this.dayOfWeek.join(''));

  }



  submit(){
    this.createListingForm.date = new Date();
    this.createListingForm.dayOfWeek = this.dayOfWeek.join('');


  }

}
<div class="container-fluid mt-4 ps-5">
  <div class="row mb-3">
    <div class="col-12 title">
      Post a listing to find your private tutor!
    </div>
  </div>
  <div class="row">
      <div class="col-sm-4 mb-3">
          <div class="subtitle mb-3">Academic Level</div>
          <select class="form-control" [(ngModel)]="createListingForm.acadLvl" disabled>
            <option *ngFor="let acadLvl of academicLvlList" [value]="acadLvl.value">{{ acadLvl.label }}</option>
          </select>
      </div>
      <div class="col-sm-4 mb-3">
        <div class="subtitle mb-3">Subject</div>
        <select class="form-select" [(ngModel)]="createListingForm.acadSubject">
          <option value="">Please select</option>
          <option *ngFor="let subj of academicLvlSubjectList" [value]="subj">{{ academicSubjectList.get(subj) }}</option>
        </select>
    </div>
  </div>
  <div class="row">
    <div class="col-sm-4 mb-3">
      <div class="subtitle mb-3">Postal Code</div>
        <input type="search" class="form-control" [(ngModel)]="createListingForm.postalCode" disabled/>
    </div>
    <div class="col-sm-4 mb-3">
      <div class="subtitle mb-3">Address</div>
      <input type="search" class="form-control" [(ngModel)]="address" disabled/>
    </div>
  </div>
  <div class="row">
    <div class="col-12">
      <div class="subtitle mb-3">Day and Time of Week</div>
      <table style="width:80%">
        <thead>
          <th>Day</th>
          <th>Start Hour</th>
          <th>Start Minute</th>
          <th>AM/PM</th>
          <th>End Hour</th>
        </thead>
        <tbody>
          <td>1</td>
          <td>2</td>
          <td>3</td>
          <td>4</td>
        </tbody>
      </table>
      <div class="form-check">
        <div class="row">
          <div class="col-sm-1 mb-4">
            <input class="form-check-input" type="checkbox" (change)="flipDayIndex(0)"><label>Monday</label>
          </div>
          <div class="col-sm-2 mb-4">
            <select class="form-select" [(ngModel)]="createListingForm.acadSubject">
              <option value="">Please select</option>
              <option *ngFor="let subj of academicLvlSubjectList" [value]="subj">{{ academicSubjectList.get(subj) }}</option>
            </select>
          </div>
        </div>

        <div class="mb-4">
          <input class="form-check-input" type="checkbox" (change)="flipDayIndex(1)"><label>Tuesday</label>
        </div>
        <div class="mb-4">
          <input class="form-check-input" type="checkbox" (change)="flipDayIndex(2)"><label>Wednesday</label>
        </div>
        <div class="mb-4">
          <input class="form-check-input" type="checkbox" (change)="flipDayIndex(3)"><label>Thursday</label>
        </div>
        <div class="mb-4">
          <input class="form-check-input" type="checkbox" (change)="flipDayIndex(4)"><label>Friday</label>
        </div>
        <div class="mb-4">
          <input class="form-check-input" type="checkbox" (change)="flipDayIndex(5)"><label>Saturday</label>
        </div>
        <div class="mb-4">
          <input class="form-check-input" type="checkbox" (change)="flipDayIndex(6)"><label>Sunday</label>
        </div>
      </div>
    </div>
  </div>

</div>
