import { ACADEMICLEVELSUBJECTLIST, ACADEMICLEVELIDLIST, AcademicLevelSubject } from './../../../utilities/dropdown-values/AcademicLevelSubjectList';
import { Component } from '@angular/core';
import { CreateListingForm } from 'src/app/DTO/CreateListingForm';
import { CodeTable } from 'src/app/utilities/dropdown-values/CodeTable';

@Component({
  selector: 'app-create-listing',
  templateUrl: './create-listing.component.html',
  styleUrls: ['./create-listing.component.css']
})
export class CreateListingComponent {

  createListingForm: CreateListingForm = new CreateListingForm();
  academicLvlList: CodeTable[] = ACADEMICLEVELIDLIST;
  academicLvlSubjectList: string[];


  ngOnInit(){
    //call userservice or get from storage to get user academic level
    this.createListingForm.acadLvl = "J1";
    ACADEMICLEVELSUBJECTLIST.forEach((lvlSubj) => {
      if(lvlSubj.acadLvlId === this.createListingForm.acadLvl){
        this.academicLvlSubjectList = lvlSubj.acadSubjectIdList;
      }
    })

  }

}
<div class="container-fluid mt-4 ps-5">
  <div class="row mb-3">
    <div class="col-12 title">
      Post a listing to find your private tutor!
    </div>
  </div>
  <div class="row">
      <div class="col-sm-4 mb-2">
          <div class="subtitle mb-2">Academic Level</div>
          <select class="form-select" [(ngModel)]="createListingForm.acadLvl" disabled>
            <option *ngFor="let acadLvl of academicLvlList" [value]="acadLvl.value">{{ acadLvl.label }}</option>
          </select>
      </div>
      <div class="col-sm-4 mb-2">
        <div class="subtitle mb-2">Subject</div>
        <select class="form-select" [(ngModel)]="createListingForm.acadLvl">
          <option *ngFor="let subj of academicLvlSubjectList" [value]="subj">{{ subj }}</option>
        </select>
    </div>
  </div>
</div>
